import React, { useContext, useState } from 'react';
import { UsuarioContext } from "../../contexts/UsuarioContext";
import { CursosContext } from "../../contexts/CursosContext";
import axios from 'axios';
import { useForm } from 'react-hook-form';
import Alert from 'react-bootstrap/Alert';

export default function Producto() {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, setCursos] = useContext(CursosContext);
    const { register, handleSubmit, errors } = useForm();
    const [mensajeError, setMensajeError] = useState("");

    const mostrarMensajeError = () => {
        setMensajeError("Error al agregar curso");
    }

    const onSubmit = (data) => {
        const curso = {
            "semestre": data.semestre,
            "nombre": data.nombre,
            "clave": data.clave,
            "unidades": data.unidades,
        }
        const registarCurso = () => {
            axios.post('http://localhost:5000/cursos/add', curso)
                .then(res => {
                    console.log(res);
                    asignarCursoAUsuario(usuario._id, res);
                })
                .catch(e => {
                    console.log(e);
                    mostrarMensajeError();
                });
        }

        const asignarCursoAUsuario = async (idUsuario, res) => {
            axios.post('http://localhost:5000/usuarios/relCurso', { "usuario": idUsuario, "curso": res })
                .then(res => {
                    console.log(res);
                    borrarCampos();
                    setShow(true);
                    establecerCursosEnContext();
                })
                .catch(e => {
                    console.log(e);
                    mostrarMensajeError();
                });
        }

        const establecerCursosEnContext = () => {
            const idUsuario = usuario._id;
            axios.get('http://localhost:5000/cursos/obtenerCursos/', { headers: { authorization: idUsuario } })
                .then(res => {
                    console.log(res);
                    setCursos(res.data)
                })
                .catch(e => {
                    console.log(e);
                })
        }

        const borrarCampos = () => {
            document.getElementById("forma-productos").reset();
        }

        registarCurso();
    }

    const [show, setShow] = useState(false);
    function AlertaExito() {
        if (show) {
            return (
                <Alert variant="success" onClose={() => setShow(false)} dismissible>
                    <Alert.Heading>Registro exitoso</Alert.Heading>
                </Alert>
            );
        }
        return (<div></div>);
    }

    return (
        <div className="wrap">
            <div className="formulario">
                <h1>Registro de nuevo curso</h1>
                <form id="forma-productos" onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-group d-flex flex-column justify-content-center">
                        <input
                            type="text"
                            placeholder="Nombre: NVIDIA GEFORCE GTX 1050ti"
                            className="form-control box"
                            name="nombre"
                            ref={register({ required: "*Nombre requerido", minLength: { value: 6, message: "Min 6 caracteres" }, maxLength: { value: 20, message: "Max 20 caracteres" } })}
                        ></input>
                        {errors.nombre && <p className="error">{errors.nombre.message}</p>}
                        <input
                            type="text"
                            placeholder="Precio(MX): 3500"
                            className="form-control box"
                            name="precio"
                            ref={register({ required: "*Precio requerido", pattern: { value: /^[0-9\s]+$/, message: "Solo numeros" }, minLength: { value: 4, message: "Min 4 caracteres" }, maxLength: { value: 20, message: "Max 20 caracteres" } })}
                        ></input>
                        {errors.precio && <p className="error">{errors.precio.message}</p>}
                        <input
                            type="text"
                            placeholder="Categoría: Tarjetas gráficas"
                            className="form-control box"
                            name="categoria"
                            ref={register({ required: "*Categoría requerida", minLength: { value: 4, message: "Min 4 caracteres" }, maxLength: { value: 15, message: "Max 15 caracteres" } })}
                        ></input>
                        {errors.categoria && <p className="error">{errors.categoria.message}</p>}
                    </div>
                    <div className="form-group d-flex flex-column justify-content-center">
                        <input type="submit" value="Registrar curso" className="btn-secondary"></input>
                    </div>
                </form>
                <p className="error">{mensajeError}</p>
                <AlertaExito />
            </div>
        </div>
    );

}
