import React,{useState, createContext, useEffect} from 'react';
import axios from 'axios';

export const UsuarioContext = createContext();

export const UsuarioProvider = (props) => {
    const [usuario, setUsuario] = useState({});

    window.addEventListener("storage", (err)=>{
        console.log("OLI:"+localStorage.getItem('token'));
    }); 
    
    function establecerUsuario(usuario) {
        setUsuario(usuario);
    }

    const obtenerUsuario = ()=> {
        // const obtenerToken = async()=>{
        //     return await localStorage.getItem('token');
        // }
        // const token = obtenerToken();
        // console.log(token);


        axios.get('http://localhost:5000/usuarios/', {headers:{authorization:localStorage.getItem('token')}})
        .then(res => { 
          console.log(res);
          establecerUsuario(res.data)
        })
        .catch(e => {        
              console.log(e);
        })
    }
    
    useEffect(obtenerUsuario, []);  

    return (
        <UsuarioContext.Provider value ={[usuario, setUsuario]}>
            {props.children}
        </UsuarioContext.Provider>
    );
    
}
