import React, {useContext, useState} from 'react';
import {UsuarioContext} from "../auth/UsuarioContext";


export default function Cursos () {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, setCursos] = useState([]);

    const obtenerCursos = ()=> {
        axios.get('http://localhost:5000/cursos/', usuario._id)
        .then(res => { 
            console.log(res);
            establecerUsuario(res.data)
        })
        .catch(e => {        
            console.log(e);
        })  
             
    }

    function establecerCursos(cursos) {
        setCursos(cursos);
    }

    useEffect(obtenerCursos, []); 

    if(usuario.cursos.length <= 0) {
        return (
            <div>
                <h1>Aun no tiene cursos registrados</h1>
            </div>
        );
    }
    
    return (      
        <div>
            <h1>Cursos registrados: </h1>
            {usuario.cursos.map(item => {
                return <p>{item}</p>;
            })}

        </div>
    );
    
}
