import React, {Component} from 'react';

export default class Cursos extends Component {

    constructor(props) {
        super(props);

        this.onChangeNombre = this.onChangeNombre.bind(this);
        this.onChangeClave = this.onChangeClave.bind(this);
        this.onChangeUnidades = this.onChangeUnidades.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            nombre : "",
            clave: "",
            unidades: 0,
        }
    }

    onChangeNombre(e) {
        this.setState({
            nombre: e.target.value
        });
    }

    onChangeClave(e) {
        this.setState({
            clave: e.target.value
        });
    }

    onChangeUnidades(e) {
        this.setState({
            unidades: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const curso = {
            nombre: this.state.nombre,
            clave: this.state.clave,
            unidades: this.state.unidades,
        }

        console.log(curso);

        window.location = '/';
    }

    render() {
        return (
            <div>
                <h3>Registro de un nuevo curso</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Nombre: </label>
                        <input type="text" 
                            required
                            className="form-control"
                            value={this.state.nombre}
                            onChange={this.onChangeNombre}/>
                    </div>
                    <div className="form-group">
                        <label>Clave: </label>
                        <input type="text" 
                            required
                            className="form-control"
                            value={this.state.clave}
                            onChange={this.onChangeClave}/>
                    </div>
                    <div className="form-group">
                        <label>Unidades: </label>
                        <input type="text" 
                            required
                            className="form-control"
                            value={this.state.unidades}
                            onChange={this.onChangeUnidades}/>
                    </div>
                    <div className="form-group">
                        <input type="submit" 
                            className="btn btn-primary"
                            value="Registrar curso"/>
                    </div>
                </form>
            </div>
        );
    }
}
