import React, { useContext, useState } from 'react';
import { UsuarioContext } from "./auth/UsuarioContext";
import axios from 'axios';
import {useHistory} from 'react-router-dom';
import {useForm} from 'react-hook-form'

export default function Perfil(props) {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const {register, handleSubmit, errors} = useForm();  
    const history = useHistory();

    const onSubmit  = (datos) => { 
        const modificarUsuario = ()=> {            
            axios.put('http://localhost:5000/usuarios/modificar', {
                data: {
                    usuario: usuario._id,
                    nombre: datos.nombre,
                    id:datos.id,       
                    nomInstitucion: datos.nomInstitucion,
                }
            })
            .then(res => {
                console.log(res);
                setUsuario(res);
            })
            .catch(e => {
                console.log(e);
            }); 
        }  

        modificarUsuario();
    }

    return (
        <div className="wrap">
            <div className="container formulario">
                <h1>Perfil</h1>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-group d-flex flex-column justify-content-center">
                        <div>ID:</div>
                        <input 
                            type="text" 
                            placeholder={usuario.id} 
                            className="form-control box" 
                            name="id"
                            ref={register({required:"*ID requerido", minLength:{value:4, message:"Min 4 caracteres"}, maxLength:{value:20, message:"Max 20 caracteres"}})}
                        ></input>
                        {errors.id && <p className="error">{errors.id.message}</p>}
                        <div>Nombre:</div>
                        <input 
                            type="text" 
                            placeholder={usuario.nombre} 
                            className="form-control box" 
                            name="nombre"
                            ref={register({required:"*Nombre requerido",  pattern:{value: /^[a-zA-Z\s]+$/, message:"Solo letras"}, minLength:{value:4, message:"Min 4 caracteres"}})}
                        ></input>
                        {errors.name && <p className="error">{errors.name.message}</p>}
                        <div>Nombre Instituci√≥n:</div>
                        <input 
                            type="text" 
                            placeholder={usuario.nomInstitucion} 
                            className="form-control box" 
                            name="nomInstitucion"
                            ref={register({required:"*Nombre institucion requerido", minLength:{value:4, message:"Min 4 caracteres"}, maxLength:{value:20, message:"Max 20 caracteres"}})}
                        ></input>
                        {errors.nomInstitucion && <p className="error">{errors.nomInstitucion.message}</p>}
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Guardar" className="btn btn-primary"></input>
                    </div>
                </form>
                {/* <p className="error">{mensajeError}</p> */}
            </div>
        </div>
    );
}
