export const filtrarDatos = (datos) => {
    var curso = '';
    var fecha = '';
    var alumnos = [];

    return new Promise((resolve, reject) => {
        curso = datos[0][0].split(' ')[2];

        curso = curso.substring(15, curso.length);

        fecha = datos[2][1];
        fecha = new Date(fecha);

        for (var i = 3; i < datos.length; i++) {
            const nombre = datos[i][0];
            var asistencia = false;
            if (datos[i].length > 1) {
                if (datos[i][1].length == 1) {
                    asistencia = true;
                }
            }
            const alumno = [nombre, asistencia];
            alumnos.push(alumno);
        }

        console.log(curso);
        console.log(fecha);
        console.log(alumnos);
    });
};
