import React, {useState, useContext} from 'react';
import {UsuarioContext} from './auth/UsuarioContext';
import axios from 'axios';

export default function Perfil() {    
    const [usuario, setUsuario] = useState({});    
    //const idUsuario = '5fa3113687bb91324014eee2';
    const idUsuario = localStorage.getItem('token');

    function establecerUsuario(usuario) {
        setUsuario(usuario);
    }

    axios.get('http://localhost:5000/usuarios/'+idUsuario)
      .then(res => {
        console.log(res);
        establecerUsuario(res.data)
      })
      .catch(e => {        
        console.log(e);
      }); 

    return (
        <div>
            <h1>{usuario.nombre}</h1>
            
            
        </div>
    );
    
}
