import React, { useState, useContext } from 'react';
import Form from 'react-bootstrap/Form'
import { UsuarioContext } from "../../contexts/UsuarioContext";
import { CursosContext } from "../../contexts/CursosContext";
import { DatePicker } from "react-bootstrap-date-picker";
//var DatePicker = require("react-bootstrap-date-picker");
var DayPicker = React.createClass({
    getInitialState: function () {
        var value = new Date().toISOString();
        return {
            value: value
        }
    },
    handleChange: function (value, formattedValue) {
        this.setState({
            value: value, // ISO String, ex: "2016-11-19T12:00:00.000Z"
            formattedValue: formattedValue // Formatted String, ex: "11/19/2016"
        });
    },
    componentDidUpdate: function () {
        // Access ISO String and formatted values from the DOM.
        var hiddenInputElement = document.getElementById("example-datepicker");
        console.log(hiddenInputElement.value); // ISO String, ex: "2016-11-19T12:00:00.000Z"
        console.log(hiddenInputElement.getAttribute('data-formattedvalue')) // Formatted String, ex: "11/19/2016"
    },
    render: function () {
        return <FormGroup>
            <ControlLabel>Label</ControlLabel>
            <DatePicker id="example-datepicker" value={this.state.value} onChange={this.handleChange} />
            <HelpBlock>Help</HelpBlock>
        </FormGroup>;
    }
});


export default function Asistencias() {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, setCursos] = useContext(CursosContext);
    //const [cursos, setCursos] = useState(usuario.cursos);

    return (
        <div>
            <h1>Registro de asistencias</h1>
            <Form>
                <Form.Group id="seleccionar-curso">
                    <Form.Label>Curso</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione un curso...</option>
                        {cursos.map((curso, idx) =>
                            <option key={curso._id}>{curso.nombre}</option>
                        )}
                    </Form.Control>
                </Form.Group>
            </Form>

            <Form>
                <Form.Group id="seleccionar-grupo">
                    <Form.Label>Grupo</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione un grupo...</option>
                    </Form.Control>
                </Form.Group>
            </Form>

            <Form>
                <Form.Group id="seleccionar-unidad">
                    <Form.Label>Unidad</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione una unidad...</option>
                    </Form.Control>
                </Form.Group>
            </Form>

            <DayPicker />

            <Form>
                <Form.File
                    id="custom-file"
                    label="Custom file input"
                    custom
                />
            </Form>

        </div>
    )
}
