import React, {useContext, useState, useEffect} from 'react';
import {UsuarioContext} from "../auth/UsuarioContext";
import axios from 'axios';

import eliminar from '../../assets/delete.png';
import ver from '../../assets/lookup.png';
import editar from '../../assets/edit.png';

export default function Cursos () {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, setCursos] = useState([{}]);
    

    const obtenerCursos = ()=> {
        const idUsuario = usuario._id;
        axios.get('http://localhost:5000/cursos/obtenerCursos/',{headers:{authorization:idUsuario}})
        .then(res => { 
            console.log(res);
            establecerCursos(res.data)
        })
        .catch(e => {        
            console.log(e);
        })               
    }

    const eliminarCurso = (idCurso)=> {
        console.log('eliminar');              
        axios.delete('http://localhost:5000/cursos/eliminar/', {
            data: {
              usuario:usuario._id,
              curso:idCurso
            }
          })
        .then(res => { 
            console.log(res);
            obtenerCursos();
        })
        .catch(e => {        
            console.log(e);
        })               
    }

    const modificarCurso = ()=> {
        console.log('modificar');
        // const idUsuario = usuario._id;        
        // axios.delete('http://localhost:5000/cursos/eliminar/',{"usuario":idUsuario, "curso":idCurso})
        // .then(res => { 
        //     console.log(res);
        //     //establecerCursos(res.data)
        // })
        // .catch(e => {        
        //     console.log(e);
        // })               
    }

    const verCurso = ()=> {
        console.log('Ver curso');          
    }

    function establecerCursos(cursos) {
        setCursos(cursos);
    }

    //HACER CONTEXT DE CURSOS
    useEffect(obtenerCursos, []); 
    
    return (      
        <div className="mx-auto" >
            <h1>Cursos registrados: </h1>  

            <table class="table">
            <thead class="thead-dark">
                <tr>
                <th scope="col">Período</th>
                <th scope="col">Nombre</th>
                <th scope="col">Clave</th>
                <th scope="col">Unidades</th>
                <th scope="col" colSpan="3">Acciones</th>
                </tr>
            </thead>
            <tbody>
                {
                cursos.map(curso => {
                    return (       
                        <tr>
                        <th scope="row">{curso.semestre}</th>
                        <td>{curso.nombre}</td>
                        <td>{curso.clave}</td>
                        <td>{curso.unidades}</td>
                        <td className="button" id="btnDelete"><img src={eliminar} width="25px" alt="Eliminar" onClick={()=>eliminarCurso(curso._id)}/></td>
                        <td className="button" id="btnLookup"><button><img src={ver} alt="Ver" onClick={()=>verCurso()} /></button></td>
                        <td className="button" id="btnEdit"><img src={editar} width="25px" alt="Editar" onClick={()=>modificarCurso()}/></td>
                        </tr>
                    )
                })                
                }                
            </tbody>
            </table>
        
        </div>
    );
    
}
