import React, { useState, createContext, useEffect, useContext } from 'react';
import { UsuarioContext } from './UsuarioContext';
import { CursosContext } from './CursosContext';
import axios from 'axios';

export const AsistenciasContext = createContext();

export const AsistenciasProvider = (props) => {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, setCursos] = useState(CursosContext);
    const [asistencias, setAsistencias] = useState([]);

    const obtenerAsistencias = () => {
        if (cursos == undefined) return;
        let cursosIds = [];
        cursos.array.forEach(element => {
            console.log(element);
        });
        //cursos.map(curso => cursosIds.push(curso._id));

        console.log(cursosIds);
        //if (cursos != undefined) {
        //let arregloAsistencias = [];
        // cursos.map((curso) => {
        //console.log('cursos' + usuario.cursos);
        axios.get('http://localhost:5000/asistencias/obtenerAsistencias/', { headers: { authorization: usuario.cursos } })
            .then(res => {
                //console.log(res.data);
                //arregloAsistencias.push(res.data);
                establecerAsistencias(res.data);
                //establecerAsistencias(res.data);
            })
            .catch(e => {
                console.log(e);
            })
        // });

        //console.log(asistencias);
        //}
    }

    function establecerAsistencias(asistencias) {
        setAsistencias(asistencias);
    }

    useEffect(obtenerAsistencias, [usuario]);

    return (
        <AsistenciasContext.Provider value={[asistencias, obtenerAsistencias]}>
            {props.children}
        </AsistenciasContext.Provider>
    );

}
