import React, {useContext, useState} from 'react';
import {UsuarioContext} from "../auth/UsuarioContext";
import axios from 'axios';
import {useForm} from 'react-hook-form'


export default function Cursos () {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const {register, handleSubmit, errors} = useForm();  
    const [mensajeError, setMensajeError] = useState("");

    const mostrarMensajeError = ()=>{
        setMensajeError("Error al agregar curso");
    }

    const onSubmit  = (data) => {        
        const curso = {
            "semestre":data.semestre,
            "nombre":data.nombre,
            "clave":data.clave,
            "unidades":data.unidades,
        }        

        // axios.post('http://localhost:5000/relCurso', curso)
        //     .then(res => {
        //         console.log(res);
        //     })
        //     .catch(e => {
        //         console.log(e);
        //         mostrarMensajeError();
        //     });  
    }

    return (
        <div className="wrap">
            <div className="formulario">
                <h1>Registro de nuevo curso</h1>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-group d-flex flex-column justify-content-center">
                        <input 
                            type="text" 
                            placeholder="Semestre: Ago-Dic 2020" 
                            className="form-control box" 
                            name="semestre"
                            ref={register({required:"*Semestre requerido", minLength:{value:4, message:"Min 4 caracteres"}, maxLength:{value:20, message:"Max 20 caracteres"}})}
                        ></input>
                        {errors.semestre && <p className="error">{errors.semestre.message}</p>}
                        <input 
                            type="text" 
                            placeholder="Nombre: Programacion I" 
                            className="form-control box" 
                            name="nombre"
                            ref={register({required:"*Nombre requerido", minLength:{value:4, message:"Min 4 caracteres"}, maxLength:{value:20, message:"Max 20 caracteres"}})}
                        ></input>
                        {errors.nombre && <p className="error">{errors.nombre.message}</p>}
                        <input 
                            type="text" 
                            placeholder="Clave: PRO-1234" 
                            className="form-control box" 
                            name="clave"
                            ref={register({required:"*Clave requerida", minLength:{value:4, message:"Min 4 caracteres"}, maxLength:{value:10, message:"Max 10 caracteres"}})}
                        ></input>
                        {errors.clave && <p className="error">{errors.clave.message}</p>}
                        <input 
                            type="number" 
                            placeholder="Unidades: 3" 
                            className="form-control box" 
                            name="unidades"
                            ref={register({required:"*Unidades requeridas", minLength:{value:1, message:"Min 1 caracteres"}})}
                        ></input>
                        {errors.unidades && <p className="error">{errors.unidades.message}</p>}
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Registrar curso" className="btn btn-primary"></input>
                    </div>
                </form>
                <p className="error">{mensajeError}</p>
            </div>
        </div>
    );
    
}
