import React, { useState, useContext } from 'react';
import Form from 'react-bootstrap/Form'
import { UsuarioContext } from "../../contexts/UsuarioContext";
import { CursosContext } from "../../contexts/CursosContext";

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import es from 'date-fns/locale/es';


const Calendar = () => {
    const [startDate, setStartDate] = useState(new Date());
    return (
        <DatePicker
            selected={startDate}
            onChange={date => setStartDate(date)}
            locale={es}
            showTimeSelect
            timeFormat='p'
            timeCaption="Hora"
            dateFormat="dd/MM/yyyy h:mm aa"
        />
    );
};

export default function Asistencias() {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, setCursos] = useContext(CursosContext);
    //const [cursos, setCursos] = useState(usuario.cursos);

    return (
        <div>
            <h1>Registro de asistencias</h1>
            <Form>
                <Form.Group id="seleccionar-curso">
                    <Form.Label>Curso</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione un curso...</option>
                        {cursos.map((curso, idx) =>
                            <option key={curso._id}>{curso.nombre}</option>
                        )}
                    </Form.Control>
                </Form.Group>



                <Form.Group id="seleccionar-grupo">
                    <Form.Label>Grupo</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione un grupo...</option>
                    </Form.Control>
                </Form.Group>



                <Form.Group id="seleccionar-unidad">
                    <Form.Label>Unidad</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione una unidad...</option>
                    </Form.Control>
                </Form.Group>


                <Form.Label>Fecha</Form.Label>
                <br />
                <Calendar />
                <br />
                <br />

                <h3>Registro</h3>

                <table class="table">
                    <thead class="thead-dark">
                        <tr>
                            <th scope="col">Alumno</th>
                            <th scope="col">Asistencia</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            cursos.map(curso => {
                                return (
                                    <tr>
                                        <td>
                                            <Form.Control type="" placeholder="Enter email" />
                                            <input className="editData" ></input>
                                        </td>
                                        <td>{curso.nombre}</td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>


                <div className="mb-3">
                    <Form.File id="formcheck-api-custom" custom>
                        <Form.File.Input isValid />
                        <Form.File.Label data-browse="Button text">
                            Custom file input
                            </Form.File.Label>
                        <Form.Control.Feedback type="valid">You did it!</Form.Control.Feedback>
                    </Form.File>
                </div>
                <div className="mb-3">
                    <Form.File id="formcheck-api-regular">
                        <Form.File.Label>Regular file input</Form.File.Label>
                        <Form.File.Input />
                    </Form.File>
                </div>
            </Form>



        </div>
    )
}
