import React, { useContext, useState, useEffect } from 'react';
import { UsuarioContext } from "../../contexts/UsuarioContext";
import { CursosContext } from "../../contexts/CursosContext";
import { AsistenciasContext } from "../../contexts/AsistenciasContext";
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import { useFieldArray } from 'react-hook-form';

export default function Asistencias() {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, setCursos] = useContext(CursosContext);
    const [asistencias, obtenerAsistencias] = useContext(AsistenciasContext);
    //const [asistencias, setAsistencias] = useState([]);

    const history = useHistory();

    // const obtenerAsistencias = () => {
    //     if (cursos != undefined) {
    //         let arregloAsistencias = [];
    //         cursos.map((curso) => {
    //             axios.get('http://localhost:5000/asistencias/obtenerAsistencias/', { headers: { authorization: curso._id } })
    //                 .then(res => {
    //                     //console.log(res.data);
    //                     arregloAsistencias.push(res.data);
    //                     //establecerAsistencias(res.data);
    //                 })
    //                 .catch(e => {
    //                     console.log(e);
    //                 })
    //         });
    //         establecerAsistencias(arregloAsistencias)
    //         console.log(arregloAsistencias);
    //     }
    // }

    function establecerAsistencias(asistencias) {
        setAsistencias(asistencias);
    }

    function encontrarCurso(idCurso) {
        return cursos.find(curso => curso._id == idCurso);
    }
    //HACER CONTEXT DE CURSOS
    //useEffect(obtenerAsistencias, []);
    //obtenerAsistencias();

    return (
        <div className="mx-auto table-dark" >
            <h1>Asistencias: </h1>
            <table className="table">
                <thead className="thead-dark">
                    <tr>
                        <th scope="col">Curso</th>
                        <th scope="col">Fecha</th>
                        <th scope="col">Alumno</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        asistencias.map((asistencia, idx) => {
                            return (
                                <tr key={idx}>
                                    <td>{encontrarCurso(asistencia._id)}</td>
                                    <td>{asistencia.fecha}</td>
                                    {
                                        asistencia.registro.map((reg, idx) => {
                                            return (
                                                <tr key={idx}>{reg}</tr>
                                            )
                                        })
                                    }
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
        </div>

    );

}
