import React, { useState, useContext, useEffect } from 'react';
import Form from 'react-bootstrap/Form'
import { UsuarioContext } from "../../contexts/UsuarioContext";
import { CursosContext } from "../../contexts/CursosContext";

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import es from 'date-fns/locale/es';
import Button from 'react-bootstrap/Button';

import { useForm } from 'react-hook-form';
import axios from 'axios';

const Calendar = () => {
    const [startDate, setStartDate] = useState(new Date());
    return (
        <DatePicker
            selected={startDate}
            onChange={date => setStartDate(date)}
            locale={es}
            showTimeSelect
            timeFormat='p'
            timeCaption="Hora"
            dateFormat="dd/MM/yyyy h:mm aa"
        />
    );
};

export default function Asistencias() {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, setCursos] = useContext(CursosContext);
    const [alumnos, setAlumnos] = useState([{ nombre: '', asistencia: false }]);
    const { register, handleSubmit, errors } = useForm();

    const onSubmit = (data) => {
        const curso = {
            "semestre": data.semestre,
            "nombre": data.nombre,
            "clave": data.clave,
            "unidades": data.unidades,
        }
        const registarCurso = () => {
            axios.post('http://localhost:5000/asistencias/add', curso)
                .then(res => {
                    console.log(res);
                })
                .catch(e => {
                    console.log(e);
                });
        }
        registarCurso();
    }

    const agregarAlumno = () => {
        if (alumnos.length < 40) {
            establecerAlumnos({
                nombre: '',
                asistencia: false
            });
        } else {
            console.log("Demasiados alumnos");
        }
    }

    const establecerAlumnos = (alum) => {
        const copia = [...alumnos, alum];
        setAlumnos(copia);
        console.log(alumnos);
    }

    const handleBlur = (tags, tipo) => (event) => {
        const copia = alumnos;
        if (tipo == 0) {
            const nombre = event.target.value;
            copia[tags].nombre = nombre;
        } else {
            var asistencia = false;
            if (event.target.checked) {
                asistencia = true;
            }
            copia[tags].asistencia = asistencia;
        }
        setAlumnos(copia);
    }

    const RegistroAlumnos = () => {
        return (
            <tbody>{
                alumnos.map((alumno, idx) =>
                    <tr key={idx} >
                        <td>
                            <Form.Control
                                type="text"
                                className="form-control box"
                                onBlur={handleBlur(idx, 0)}
                                id={`text-${idx}`}
                                defaultValue={alumno.nombre}
                            />
                        </td>
                        <td>
                            <Form.Check
                                custom
                                id={`checkbox-${idx}`}
                                onBlur={handleBlur(idx, 1)}
                                defaultChecked={alumno.asistencia}
                            />
                        </td>
                    </tr>
                )}</tbody>
        );
    }

    useEffect(RegistroAlumnos, [alumnos]);

    return (
        <div>
            <h1>Registro de asistencias</h1>
            <Form onSubmit={handleSubmit(onSubmit)}>
                <Form.Group id="seleccionar-curso">
                    <Form.Label>Curso</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione un curso...</option>
                        {cursos.map((curso, idx) =>
                            <option key={curso._id}>{curso.nombre}</option>
                        )}
                    </Form.Control>
                </Form.Group>

                <Form.Group id="seleccionar-grupo">
                    <Form.Label>Grupo</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione un grupo...</option>
                    </Form.Control>
                </Form.Group>

                <Form.Group id="seleccionar-unidad">
                    <Form.Label>Unidad</Form.Label>
                    <Form.Control as="select" custom>
                        <option>Seleccione una unidad...</option>
                    </Form.Control>
                </Form.Group>

                <Form.Label>Fecha</Form.Label>
                <br />
                <Calendar />
                <br />
                <br />

                <h3>Registro</h3>

                <table className="table" id="tablaRegistro">
                    <thead className="thead-dark">
                        <tr>
                            <th scope="col">Alumno</th>
                            <th scope="col">Asistencia</th>
                        </tr>
                    </thead>
                    <RegistroAlumnos />
                </table>
                <Button onClick={agregarAlumno} variant="dark">Aniadir alumno</Button>

                <br />
                <br />

                <div className="mb-3">
                    <Form.File id="formcheck-api-regular">
                        <Form.File.Label>Generar asistencia con CSV</Form.File.Label>
                        <Form.File.Input />
                    </Form.File>
                </div>

                <div className="form-group d-flex flex-column justify-content-center">
                    <input type="submit" value="Registrar asistencia" className="btn btn-dark"></input>
                </div>
            </Form>
        </div>
    )

}
