import React, {useContext} from 'react';
import {UsuarioContext} from "../auth/UsuarioContext";
import {MenuCursos} from "./menuCursos.component";


export default function Cursos () {
    const [usuario, setUsuario] = useContext(UsuarioContext);

    if(usuario.cursos.length <= 0) {
        return (
            <div>
                <h1>Aun no tiene cursos registrados</h1>
            </div>
        );
    }
    
    return (      
        <div>
            <h1>Cursos registrados: </h1>
            <h1>{usuario.nombre}</h1>
            <h1>{usuario.email}</h1>
            <h1>{usuario.cursos.map(curso =>{
                <p>{curso.nombre}</p>
            })}</h1>
        </div>
    );
    
}
