import React, { useContext, useState, useEffect } from 'react';
import { UsuarioContext } from "../../contexts/UsuarioContext";
import { CursosContext } from "../../contexts/CursosContext";
import axios from 'axios';
import { useHistory } from 'react-router-dom';

import eliminar from '../../assets/delete.png';
import editar from '../../assets/edit.png';

export default function ListarProducto(props) {
    const [usuario, setUsuario] = useContext(UsuarioContext);
    const [cursos, obtenerCursos] = useContext(CursosContext);

    const history = useHistory();

    const eliminarProducto = (idProducto) => {
        axios.delete('http://localhost:5000/cursos/eliminar/', {
            data: {
                usuario: usuario._id,
                producto: idProducto
            }
        })
            .then(res => {
                console.log(res);
                obtenerCursos();
            })
            .catch(e => {
                console.log(e);
            })
    }

    const modificarProducto = (_id, nuevoNombre, nuevoPrecio, nuevaCategoria) => {
        history.push({
            pathname: '/cursos/modificar',
            state: {
                producto: _id,
                nombre: nuevoNombre,
                precio: nuevoPrecio,
                categoria: nuevaCategoria,
            }
        });
    }

    return (
        <div table className=" container formulario table table-dark" >
            <h1>Productos registrados: </h1>

            <table className="table">
                <thead className="thead-dark">
                    <tr>
                        <th scope="col">Nombre</th>
                        <th scope="col">Precio</th>
                        <th scope="col">Categor√≠a</th>
                        <th scope="col" colSpan="3">Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        cursos.map((producto, idx) => {
                            return (
                                <tr key={idx}>
                                    <th scope="row">{producto.nombre}</th>
                                    <td>{producto.precio}</td>
                                    <td>{producto.categoria}</td>
                                    <td className="button" id="btnDelete"><img src={eliminar} width="25px" alt="Eliminar" onClick={() => eliminarProducto(producto._id)} /></td>
                                    <td className="button" id="btnEdit"><img src={editar} width="25px" alt="Editar" onClick={() => modificarProducto(producto._id, producto.nombre, producto.precio, producto.categoria)} /></td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>

        </div>
    );

}
